cur_frm.cscript.custom_full_name = function(doc){
	if(doc.full_name){
		doc.full_name = doc.full_name.charAt(0).toUpperCase() + doc.full_name.slice(1)
		refresh_field("full_name")
	}
}

frappe.ui.form.on('Sales Invoice', {
	refresh(frm) {
		// your code here
	},

	validate(frm) {
	    // get taxes and charges
		let tax = 0
		let charges = 0
		let allTabTax = cur_frm.doc.taxes
		if (allTabTax) {
		    for (let i = 0; i < allTabTax.length; i++) {
		        if (allTabTax[i].charge_type == "On Net Total") {
		            tax += allTabTax[i].base_tax_amount
		        } else {
		            charges += allTabTax[i].base_tax_amount
		        }
		    }
		    cur_frm.set_value("total_tax", tax)
		    cur_frm.set_value("total_char", charges)

		}
	}
})
cur_frm.add_fetch('tanda_tangan',  'employee_name',  'nama_tanda_tangan');

cur_frm.cscript.custom_nominal = function(doc){
	if(doc.nominal){
		doc.komisi = doc.nominal * 30 /100
		refresh_field("komisi")
	}
}

cur_frm.cscript.custom_nominal = function(doc){
	if(doc.nominal){
		doc.komisi_teknisi = doc.nominal * 30 /100
		refresh_field("komisi_teknisi")

		doc.komisi_perusahaan = doc.nominal * 70 /100
		refresh_field("komisi_perusahaan")
	}
}


cur_frm.cscript.custom_validate = function(doc, cdt, cdn){
	if(doc.rincian_pengeluaran){
	    var total_biaya = 0;
	    $.each(doc.rincian_pengeluaran,  function(i,  d) {

            d.amount = d.qty * d.nominal;
            total_biaya += d.amount;
        });

		doc.total_biaya = total_biaya;
		refresh_field("rincian_pengeluaran");
		refresh_field("total_biaya");
	}
}

frappe.ui.form.on('Form Teknisi',  {
    refresh: function(frm) {
        // use the __islocal value of doc,  to check if the doc is saved or not
        if(frm.doc.tipe_dokumen == "SPK"){
        	frm.set_df_property('tabel_nama_teknisi',  'read_only',  1);
        } else {
        	frm.set_df_property('tabel_nama_teknisi',  'read_only',  0);
        }

        refresh_field("tabel_nama_teknisi");

    },
    tipe_dokumen: function(frm) {
        // use the __islocal value of doc,  to check if the doc is saved or not
        if(frm.doc.tipe_dokumen == "SPK"){
        	frm.set_df_property('tabel_nama_teknisi',  'read_only',  1);
        } else {
        	frm.set_df_property('tabel_nama_teknisi',  'read_only',  0);
        }

        refresh_field("tabel_nama_teknisi");

    },
});

